import React, { useState } from 'react';
import axios from 'axios';
import './UserAuthentication.css'; // Import your CSS file

const UserAuthentication = ({ notes, setNotes, backup, selectAll }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [loggedInUser, setLoggedInUser] = useState(null);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://localhost:5000/login', { username, password });
            setLoggedInUser(response.data.user);
            setError('');
            setUsername('');
            setPassword('');
        } catch (error) {
            setLoggedInUser(null);
            setError('Invalid username/email or password.');
        }
    };

    const handleLogout = async () => {
        await axios.post('http://localhost:5000/logout'); // Adjusted for proper logout endpoint
        setUsername('');
        setPassword('');
        setLoggedInUser(null);
    };

    return (
        <div className="signup-container">
            <h2>Log In</h2>
            {message && <p className="message">{message}</p>}
            {loggedInUser ? (
                <div>
                    <p>Welcome, {loggedInUser.firstName}!</p>
                    <button className="button" onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <div>
                    <input
                        type="text"
                        placeholder="Username or Email"
                        className="input"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        className="input"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="button" onClick={handleLogin}>Login</button>
                    {error && <p className="error">{error}</p>}
                </div>
            )}
        </div>
    );
};

export default UserAuthentication;
