import axios from 'axios';

const UserAuthentication = ({ notes, setNotes, backup, selectAll }) => {
    const [username, serUsername] = useState('');
    const [password, setPassword] = useState('');
    const [loggedInUser, setLoggedInUser] = useState(null);
    const [error, setError] = useState('');

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://localhost:5000/login', { usernameOrEmail, password });
            setLoggedInUser(response.data.user);
            setError('');
            setUsernameOrEmail('');
            setPassword('');
        } catch (error) {
            setLoggedInUser(null);
            setError('Invalid username/email or password.');
        }
    };

    const handleLogout = () => {
        setUsernameOrEmail('');
        setPassword('');
        setLoggedInUser(null);
    };

    const handleSubmit = () => {
        if(loggedInUser == null){
            handleLogin();
        }
    };

    return (
        <div className="signup-container">
            <h2>Sign Up</h2>
            {message && <p className="message">{message}</p>}
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Username" value={username} onChange={(e) => serUsername(e.target.value)} />
                <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                <button type="submit">Log In</button>
            </form>
        </div>

    );
};

export default UserAuthentication;
