import './App.css';
import SignUp from './SignUp';
import Login from './LogIn'
import { useState } from 'react';
import CreateTask from './CreateTask';

function App() {

  const [login, setLogin] = useState(false);
  const [signup, setSignup] = useState(false);
  const [task, setTask] = useState(false);

  //------------------Signin--------------------
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [usernameSU, setUsernameSU] = useState('');
  const [email, setEmail] = useState('');
  const [passwordSU, setPasswordSU] = useState('');

  //------------------Login--------------------
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null);


  const handleLogin = () => {
    setLogin(true);
    setSignup(false);
    setTask(false);
  }

  const handleSignUp = () => {
    setSignup(true);
    setLogin(false);
    setTask(false);
  }

  const handleCreateTask = () => {
    setTask(true);
    setLogin(false);
    setSignup(false);
  }
  return (
    <div className="App">
      <button onClick={handleLogin}>Log In</button>
      <button onClick={handleSignUp}>Sign up</button>
      <button onClick={handleCreateTask}>Create Task</button>

      {signup && (
        <SignUp firstName={firstName} setFirstName={firstName} lastName={lastName} setLastName={setLastName}
                username={usernameSU} setUsername={setUsernameSU} email={email} setEmail={setEmail}
                password={passwordSU} setPassword={setPasswordSU} />
      )}

      {login && (
        <Login username={username} setUsername={setUsername} password={password} setPassword={setPassword} 
        loggedInUser={loggedInUser} setLoggedInUser={setLoggedInUser}/>
      )}

      {task && (
        <CreateTask />
      )}

    </div>
  );
}

export default App;
