import React, { useState } from 'react';
import axios from 'axios';
import './CreateTask.css';  

const CreateTask = ({ setNotes, notes, backup, setBackup }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [category, setCategory] = useState('shopping');
    const [privacy, setPrivacy] = useState('Public');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const [month, setMonth] = useState('');
    const [day, setDay] = useState('');
    const [year, setYear] = useState('');
    const handleClick = async (e) => {
        e.preventDefault();
        if (!title || !description) {
            setError('All fields are required.');
            setMessage('');
            return;
        }
        const currentDate = new Date();
        const formattedDate = `${currentDate.getFullYear()}-${('0' + (currentDate.getMonth() + 1)).slice(-2)}-${('0' + currentDate.getDate()).slice(-2)}`;
        const curr = { title, description, category, formattedDate, privacy };
        try {
            const response = await axios.post('http://localhost:5000/createnote', curr);
            setMessage(response.data.message);
            setNotes(response.data.notes); 
            setBackup(response.data.notes); 
            setTitle('');
            setDescription('');
            setPrivacy('Public');
            setCategory('shopping');
            setError('');  
        } catch (error) {
            console.error('Error creating note:', error);
            setError('Failed to create note.');
        }
    };

    return (
        <div className="create-note-container">
            <h2>Create Note</h2>
            <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
            <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)}></textarea>

            <div style={{display: 'flex', flexDirection: 'row'}}>
                <input type="text" placeholder="Day" value={day} onChange={(e) => setDay(e.target.value)} />
                <input type="text" placeholder="Month" value={month} onChange={(e) => setMonth(e.target.value)} />
                <input type="text" placeholder="Year" value={year} onChange={(e) => setYear(e.target.value)} />
            </div>

            <button type="submit" onClick={handleClick}>Create Task</button>
            {message && <p className="message">{message}</p>}
            {error && <p className="error">{error}</p>}
        </div>
    );
};

export default CreateTask;
